import Task from './Task';
import { useState, useRef } from 'react';

const TaskList = () => {
    const [tasksList, setTasksList] = useState({
        todo: [],
        inProgress: [],
        completed: []
    });

    const inputRef = useRef();
    const categoryRef = useRef();

    const addTask = () => {
        const taskName = inputRef.current.value.trim();
        const category = categoryRef.current.value;

        if (!taskName || !category) {
            alert("Please enter task name and select category");
            return;
        }

        const newTask = {
            id: Date.now(),
            name: taskName,
        };

        setTasksList((prev) => ({
            ...prev,
            [category]: [...prev[category], newTask],
        }));

        inputRef.current.value = '';
        categoryRef.current.value = '';
    };


    return (
        <>
            <input  ref={inputRef}
          type="text"
          placeholder="Add new task"
          style={{ padding: '10px', width: '250px' }} />
            <select ref={categoryRef}  style={{ marginLeft: '10px', padding: '10px' }}>
                <option value="">Select Task Category</option>
                <option value="todo">To Do</option>
                <option value="progress">In Progress</option>
                <option value="completed">Completed</option>
            </select>
            <input Type="button" value="Add Task" onClick={addTask} style={{ padding: '10px' }} />
            <div style={{ display: 'flex', justifyContent: 'space-around', }}>
                {Object.entries(tasksList).map(([category, tasks]) => (
                    <>

                         <h3 style={{ textAlign: 'center', textTransform: 'capitalize' }}>
                        {category.replace('inProgress', 'In Progress')}
                        </h3>
                        <Task tasks={tasks} ></Task>
                        </>
                ))}</div></>)
}

export default TaskList;
