import Task from './Task';
import { useState, useRef } from 'react';

const TaskList = () => {
  const [tasksList, setTasksList] = useState({
    todo: [],
    inProgress: [],
    completed: [],
  });

  const inputRef = useRef();
  const categoryRef = useRef();

  const addTask = () => {
    const taskName = inputRef.current.value.trim();
    const category = categoryRef.current.value;

    const newTask = {
      id: Date.now(),
      name: taskName,
    };

    setTasksList((prev) => ({
      ...prev,
      [category]: [...prev[category], newTask],
    }));
   localStorage.setItem('tasks', JSON.stringify({ todo: [], inProgress: [], completed: [] }));
    inputRef.current.value = '';
    categoryRef.current.value = '';
  };

  const editTask = (id, updatedName) => {

    setTasksList((prev) =>
      prev.map((task) => task.is === id ? 
           {...task , name: updatedName } : task
        )
        );
  };
  return (
    <>
      <div style={{ marginBottom: '20px' }}>
        <input
          ref={inputRef}
          type="text"
          placeholder="Add new task"
          style={{ padding: '10px', width: '250px' }}
        />
        <select ref={categoryRef} style={{ marginLeft: '10px', padding: '10px' }}>
          <option value="">Select Task Category</option>
          <option value="todo">To Do</option>
          <option value="inProgress">In Progress</option>
          <option value="completed">Completed</option>
        </select>
        <input
          type="button"
          value="Add Task"
          onClick={addTask}
          style={{ marginLeft: '10px', padding: '10px' }}
        />
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        {Object.entries(tasksList).map(([category, tasks]) => (
          <div
            key={category}
            style={{
              border: '1px solid black',
              padding: '10px',
              width: '30%',
              minHeight: '200px',
            }}
          >
            <h3 style={{ textAlign: 'center', textTransform: 'capitalize' }}>
              {category}
            </h3>

            {/* Pass tasks of each category to child */}
            <Task tasks={tasks} />
          </div>
        ))}
      </div>
    </>
  );
};

export default TaskList;
