import SearchBar from './SearchBar.jsx';
import ProductTable from './ProductTable.jsx';
import React, {useState , useEffect} from 'react';
import {useLocalStorage} from './ReactCoding/Memorization.jsx';
import {useFetchData} from'./ReactCoding/Memorization.jsx';

export default function FilterProductTable({products}) {
  const [localData, setLocalData] = useLocalStorage("searchText", "");
  const [inputText, setInputText] = useState('');
  const [inputTextTest, setInputTextTest] = useState('');
  const [isStockedOnly, setIsStockedOnly] = useState(false);
  const {data , loading, error } = useFetchData('https://jsonplaceholder.typicode.com/users')

//
  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = () => {
    console.log("inputTextTest:", inputTextTest);
    setLocalData(inputTextTest); // ✅ correct usage
     console.log("data:" , data);
  };

  const fetchData = async () => {
    console.log("data:" , data);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <>
    <div> product Data</div>
    <ul>
      {data && data.map(user => (
        <li key={user.id}>{user.name}</li>
      ))}
    </ul> </>
  );

  return (
    <>
      <input 
        value={inputTextTest} 
        onChange={(e) => setInputTextTest(e.target.value)} 
      />
      <button onClick={handleChange}>Save</button>

      <p>Local Storage Saved Value: {localData}</p>

      {/*<SearchBar 
        inputText={inputText} 
        isStockedOnly={isStockedOnly} 
        onFilterTextChange={setInputText} 
        onStockValueChane={setIsStockedOnly}
      />*/}
    </>
  );
}
