import {useRef} from "react";

export default function AddTask({onAddTask}) {
  const inputRef = useRef(null);
  const todoCategoryRef = useRef(null);

  const handleSubmit =  (e) => {
    e.preventDefault();
        let taskValue = ""
    if(inputRef.current.value.trim()){
          taskValue = inputRef.current.value;
    }

    const categoryValue = todoCategoryRef.current.value;

    onAddTask(categoryValue, { id: Date.now(), title: taskValue.trim() });

    inputRef.current.value = "";
  }
    return (
   <form onSubmit={handleSubmit} className="task-form">
    <input type="text" ref={inputRef} placeholder="Add new task..." />
      <select ref={todoCategoryRef}>
        <option value="todo">To Do</option>
        <option value="progress">In Progress</option>
        <option value="done">Done</option>
      </select>
    <button type="submit">Add Task</button>
   </form>
  );
}
