import Task from './Task';
import {useState, useRef} from 'react';

const TaskList = () => {
const [tasksList, setTasksList] = useState({
  todo: [],
  inProgress: [],
  completed: []
});

    const addTask =(category, task) => {
        setTasksList(prev => ({
            ...prev,
            [category]: [...prev[category], task]
        }))
    }

    return (
        <>
            <input Type="text" placeholder="Add new task" style={{ marginBottom: '20px', padding: '10px', width: '300px' }} />
            <select style={{ marginLeft: '10px', padding: '10px' }}>
                <option value="">Select Task Category</option>
                <option value="todo">To Do</option>
                <option value="progress">In Progress</option>
                <option value="completed">Completed</option>
            </select>
            <input Type="button" value="Add Task" onClick={addTask} style={{ padding: '10px' }} />
            <div style={{ display: 'flex', justifyContent: 'space-around', }}>
                {Object.entries(tasksList).map((item, index) => (
                    <>
                        <div style={{ border: '1px solid black', padding: '10px' }}>{item}

                            <Task addTask></Task>
                        </div></>

                ))}</div></>)
}

export default TaskList;
