export default function TaskList({ title, tasks, onEditTask }) {
  const handleEdit = (id) => {
    console.log("Editing Task:", id);

    e.preventDefault();
    const taskValue = inputRef.current.value.trim();
    const categoryValue = todoCategoryRef.current.value;

    onEditTask(categoryValue, { id: Date.now(), title: taskValue });

    inputRef.current.value = "";

    onEditTask(id);
  }
  return (
    <div >
      <h2>{title}</h2>
      {tasks.map((task, index) => (
        <span
          key={index}
          style={{ display: "flex", alignItems: "center", gap: "10px" }}
        >
          <h3>{task.id}</h3>
          <button contentEditable="true" onClick={() => handleEdit(task.id)}>Edit</button>
        </span>
      ))}
    </div>
  );
}
