import React,{useState,useMemo} from 'react';
// import Task from './Task.jsx';
import AddTask from './AddTask.jsx';
import TaskList from './TaskList.jsx';

export default function KanbanBoard({taskList}) {
    const [tasks, setTasks] = useState(taskList);

  const handleAddTask = (category, newTask) => {
    setTasks((prev) => ({
      ...prev,
      [category]: [...prev[category], newTask]
    }));
  };

   const handleEditTask = (id_) => {
    console.log("Edit requested for Task ID:", id_);

    // setTasks((prev) => ({
    //   ...prev,
    //   [category]: [...prev[category], newTask]
    // }));
  };

  return (
    <div>
    <h1>   KanbanBoard Component </h1>
      <AddTask onAddTask={handleAddTask} />
       <div className="board">
        <TaskList title="To Do" tasks={tasks.todo} onEditTask={handleEditTask} />
        <TaskList title="In Progress" tasks={tasks.progress} onEditTask={handleEditTask} />
        <TaskList title="Done" tasks={tasks.done} onEditTask={handleEditTask} />
      </div>
    </div>
  );
}

 function UseMemoExample() {
  const [count, setCount] = useState(0);
  const [text, setText] = useState("");

  // Expensive calculation simulated
  const doubleCount = useMemo(() => {
    console.log("Calculating...");
    return count * 2;
  }, [count]); // only re-run when 'count' changes

  return (
    <div>
      <h3>useMemo Example</h3>
      <p>Count: {count}</p>
      <p>Double: {doubleCount}</p>
      <button onClick={() => setCount(count + 1)}>Increase</button>
      <input value={text} onChange={(e) => setText(e.target.value)} placeholder="Type here..." />
    </div>
  );
}
