export default function Task({ tasks }) {
  const localTask = localStorage.getItem('tasks');
if (localTask) {
  try {
    const parsed = JSON.parse(localTask);
    // Only assign if it's actually an array
    if (Array.isArray(parsed)) {
      tasks = parsed;
    }
  } catch (e) {
    console.error("Error parsing tasks from localStorage:", e);
  }
}
  return (
    <>
      {
        tasks.map((item) => (
          <>
          <span
            key={item.id}
            style={{
              margin: '5px 0',
              padding: '8px',
              background: '#f4f4f4',
              borderRadius: '6px',
            }}
          >
            {item.name}
          </span>

          <button name="editTask" onClick={editTask}>Edit  </button></>
        )
      )}
    </>
  );
}
