import React,{useState} from 'react';
// import Task from './Task.jsx';
import AddTask from './AddTask.jsx';
import TaskList from './TaskList.jsx';

export default function KanbanBoard({taskList}) {
    const [tasks, setTasks] = useState({
    todo: [],
    progress: [],
    done: []
  });

  const handleAddTask = (category, newTask) => {
    console.log('Adding task to category:', newTask);
    setTasks((prev) => ({
      ...prev,
      [category]: [...prev[category], newTask]
    }));
  };
  return (
    <div>
    <h1>   KanbanBoard Component </h1>
      <AddTask onAddTask={handleAddTask} />
       <div className="board">
        <TaskList title="To Do" tasks={tasks.todo} />
        <TaskList title="In Progress" tasks={tasks.progress} />
        <TaskList title="Done" tasks={tasks.done} />
      </div>
    </div>
  );
}
