import {useRef} from "react";
export default function AddTask({onAddTask}) {
  const inputRef = useRef(null);
  const todoCategoryRef = useRef(null);

  const handleSubmit = () => {
    const taskValue = inputRef.current.value.trim();
    const categoryValue = todoCategoryRef.current.value;

    onAdd(categoryValue, { id: Date.now(), title: taskValue });
    inputRef.current.value = ""; // clear input manually
  }
    return (
   <form onSubmit={handleSubmit} className="task-form">
    <input type="text" value={inputRef} placeholder="Add new task..." />
    <select value={todoCategoryRef}>   
    <option>To DO</option>

    </select>
    <button type="submit">Add Task</button>
   </form>
  );
}
