import React,{useState,useMemo} from 'react';
// import Task from './Task.jsx';
import AddTask from './AddTask.jsx';
import TaskList from './TaskList.jsx';

export default function KanbanBoard({taskList}) {
    const [tasks, setTasks] = useState(taskList);

  const handleAddTask = (category, newTask) => {
 console.log("addd", category,newTask )
    setTasks((prev) => ({
      ...prev,
      [category]: [...prev[category], newTask]
    }));
  };

const handleEditTask = (category, taskId, newTitle) => {

     let editTask =  { id: taskId, title: newTitle.trim() }
    //console.log("edit", category, editTask)
      setTasks((prev) => ({
      ...prev,
      [category]: prev[category].map((task) =>
      task.id === taskId ? editTask : task
    )
    }));

};


  return (
    <div>
    <h1>   KanbanBoard Component </h1>
      <AddTask onAddTask={handleAddTask} />
       <div className="board"  style={{ display: "flex", gap: "20px" }}>
        <TaskList title="To Do" tasks={tasks.todo} onEditTask={handleEditTask} />
        <TaskList title="In Progress" tasks={tasks.progress} onEditTask={handleEditTask} />
        <TaskList title="Done" tasks={tasks.done} onEditTask={handleEditTask} />
      </div>
    </div>
  );
}
