import {useRef} from "react";
export default function AddTask({onAddTask}) {
  const inputRef = useRef(null);
  const todoCategoryRef = useRef(null);

  const handleSubmit = () => {
    const addText = inputRef.current.value.trim();
    const categoryText = todoCategoryRef.current.value;


    const newTask ={ id: Date.now(),
        name: addText, category: todoCategoryRef.current.value };
    onAddTask(newTask);
  }
    return (
   <form onSubmit={handleSubmit} className="task-form">
    <input type="text" value={inputRef} placeholder="Add new task..." />
    <select value={todoCategoryRef}>   
    <option>To DO</option>

    </select>
    <button type="submit">Add Task</button>
   </form>
  );
}
