];

export default function Task({addTask}) {

    const createTask =() => {
       newtask = { id: tasks.length + 1, name: `Task ${tasks.length + 1}` }
       tasks.push(newtask); 

    }

    return (<><div>{tasks.map((item) => (
        <div key={item.id}>{item.name}</div>

    ))}</div>
    <button>Add tasks</button></>
)
}
