import React, { useState, useRef } from 'react';


export  function ToDotask() {
  const [task, setTask] = useState({
    newTask: [],
    inProgres: [],
    completed: [],
  });
  const inputRef = useRef(null);
  const todoCategoryRef = useRef(null);

  const [isEdit, setISEdit] = useState(false);

  const handleAddTask = (e) => {
    e.preventDefault();

    const category = todoCategoryRef.current.value;
    const value = inputRef.current.value;

    setTask((prev) => ({ ...prev, [category]: [...prev[category], value] }));

  };

  return (
    <div>
      <form>
        <label>
          <input
            ref={inputRef}
            name="text"
            placeholder="Description"
            type="text"
          />
        </label>

        <select ref={todoCategoryRef}>
          <option value={'newTask'}>newTask</option>
          <option>inProgres</option>
          <option>completed</option>
        </select>
        <button onClick={(e) => handleAddTask(e)}> Add Task</button>
      </form>

      <div>
        <TaskList title="To Do" task={task.newTask} />
        <TaskList title="In Progress" task={task.inProgres} />
        <TaskList title="Done" task={task.completed} />
      </div>
    </div>
  );
}
