import {useState, useRef} from 'react';

export default function PasswordGenration() {
    const [password, setPassword] = useState("");
    const letterRef = useRef(null);
    const symbolRef = useRef(null);
    const numberRef = useRef(null); 

    const letters = [
        'a',
        'b',
        'c',
        'd',
        'e',
        'f',
