import React, { useState, useRef } from "react";

export default function TaskList({ title, tasks, onEditTask }) {
  const [editingTaskId, setEditingTaskId] = useState(null);
  //const [editValue, setEditValue] = useState("");

  let editInputRef = useRef(null)

  const startEdit = (task) => {
    setEditingTaskId(task.id);
    //setEditValue(task.title);
    setTimeout(() => {
      if (editInputRef.current) {
        editInputRef.current.value = task.title;
      }
    }, 0);
  };

  const submitEdit = () => {
    const newTitle = editInputRef.current.value.trim();

    onEditTask(
      title.toLowerCase().replace(" ", ""), 
      editingTaskId, 
      newTitle
    );

     setEditingTaskId(null);
  };

  return (
    <div>
      <h2>{title}</h2>
      {tasks.map((task) => (
        <div
          key={task.id}
          style={{ display: "flex", alignItems: "center", gap: "10px", marginBottom: "5px" }}
        >
          {editingTaskId === task.id ? (
            <>
              <input
                ref={editInputRef}
              />
              <button onClick={submitEdit}>Save</button>
            </>
          ) : (
            <>
              <span>{task.title}</span>
              <button onClick={() => startEdit(task)}>Edit</button>
            </>
          )}
        </div>
      ))}
    </div>
  );
}
