// import Task from './Task.jsx';
import AddTask from './AddTask.jsx';
import TaskList from './TaskList.jsx';

export default function KanbanBoard({taskList}) {
    const [inputText , setInputText] = React.useState('');
    const [todoCategory , setTodoCategory] = React.useState('To DO');

    const handleAddTask = (e) => {
      e.preventDefault();
      // Logic to add task
    }
  return (
    <div>
    <h1>   KanbanBoard Component </h1>
      <AddTask  inputText={inputText} todoCategory={todoCategory} onAddTask={handleAddTask} onSetInput={setInputText} onSetTodoCategory={setTodoCategory} />
      <TaskList list={taskList}/>
    </div>
  );
}
