export default function Task({ tasks }) {
    let localTask = localStorage.getItem('tasks');
    if (localTask) {
      tasks = JSON.parse(localTask);
    }
  return (
    <>
      {
        tasks.map((item) => (
          <div
            key={item.id}
            style={{
              margin: '5px 0',
              padding: '8px',
              background: '#f4f4f4',
              borderRadius: '6px',
            }}
          >
            {item.name}
          </div>
        )
      )}
    </>
  );
}
