import React, { useState, useRef } from 'react';
//import './style.css';

export default function App() {
  const [task, setTask] = useState({
    todo: [
      { id: 1, name: 'shilpa' },
      { id: 2, name: 'shilpaB' },
    ],
    progress: [
      { id: 4, name: 'prof' },
      { id: 3, name: 'progreeB' },
    ],
    completed: [
      { id: 5, name: 'com' },
      { id: 6, name: 'compld' },
    ],
  });
  const inputRef = useRef(null);
  const todoCategoryRef = useRef(null);

  const handleAddTask = (e) => {
    console.log(task);
    e.preventDefault();

    const category = todoCategoryRef.current.value;
    const value = inputRef.current.value;
    if (!value) return;

    let newTask = {
      id: new Date(),
      name: value,
    };

    setTask((prev) => ({ ...prev, [category]: [...prev[category], newTask] }));
  };

  const handleEditTaskList = (category, editingTaskId, editValue) => {
    inputRef.current.value = editValue;

    setTask((prev) => ({
      ...prev,
      [category]: prev[category].map((item) =>
        item.id == editingTaskId ? { ...item, name: editValue } : item
      ),
    }));
  };

  const handleDeleteTaskList = (category, child) => {
    setTask((prev) => ({
      ...prev,
      [category]: prev[category].filter(
        (deleteItem) => deleteItem.id != child.id
      ),
    }));
  };

  return (
    <div>
      <form>
        <label>
          <input
            ref={inputRef}
            name="text"
            placeholder="Description"
            type="text"
          />
        </label>

        <select ref={todoCategoryRef}>
          <option value={'todo'}>todo</option>
          <option value={'progress'}>progress</option>
          <option value={'completed'}>completed</option>
        </select>
        <button onClick={(e) => handleAddTask(e)}> Add Task</button>
      </form>

      <div>
        <TaskList
          title="To Do"
          category="todo"
          task={task.todo}
          onEditTask={handleEditTaskList}
          onDeleteTask={handleDeleteTaskList}
        />
        <TaskList
          title="In Progress"
          category="progress"
          task={task.progress}
          onEditTask={handleEditTaskList}
          onDeleteTask={handleDeleteTaskList}
        />
        <TaskList
          title="Done"
          category="completed"
          task={task.completed}
          onEditTask={handleEditTaskList}
          onDeleteTask={handleDeleteTaskList}
        />
      </div>
    </div>
  );
}
export function TaskList({ category, title, task, onEditTask, onDeleteTask }) {
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editValue, setEditValue] = useState('');

  const submitEdit = () => {
    onEditTask(category, editingTaskId, editValue);
    setEditingTaskId(null);
  };

  const startEdit = (child) => {
    setEditingTaskId(child.id);
    setEditValue(child.name);
  };

  const startDelete = (child) => {
    onDeleteTask(category, child);
  };

  return (
    <>
      <h2>{title} </h2>
      <div style={{ display: 'flex', gap: '10px' }}>
        {task.length > 0 &&
          task.map((child) => (
            <div
              key={child.id}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
                marginBottom: '5px',
              }}
            >
              {editingTaskId === child.id ? (
                <>
                  <input
                    value={editValue}
                    onChange={(e) => setEditValue(e.target.value)}
                  />
                  <button onClick={submitEdit}>Save</button>
                </>
              ) : (
                <>
                  <span>{child.name}</span>
                  <button onClick={() => startEdit(child)}>Edit</button>
                  <button onClick={() => startDelete(child)}>Delete</button>
                </>
              )}
            </div>
          ))}
      </div>
    </>
  );
}

