import SearchBar from './SearchBar.jsx';
import ProductTable from './ProductTable.jsx';
import React, {useState , useEffect} from 'react';
import {useLocalStorage} from './ReactCoding/Memorization.jsx';

export default function FilterProductTable({products}) {

  const [inputText, setInputText] = useState('');
  const [isStockedOnly, setIsStockedOnly] = useState(false);

  useEffect(() => {
    fetchData();
      const localData = useLocalStorage('searchText', inputText);
  } ,[])


  const fetchData =async ()=>{
    const response= await fetch('https://jsonplaceholder.typicode.com/users');
    const data = await response.json();
    console.log(data)
  }


  return (
    <>
      {localData.data && <div>Local Storage Data: {localData.data}</div>}
      <SearchBar inputText={inputText} isStockedOnly={isStockedOnly} onFilterTextChange={setInputText} onStockValueChane={setIsStockedOnly}/>
      <ProductTable products={products} inputText={inputText} isStockedOnly={isStockedOnly}  />
    </>
  );
}
